import React from "react";
import { useNavigate } from "react-router-dom";
import background from '../images/game.png'
import Board from './Board';
import { useState } from 'react';
import { winCalc } from '../winCalc';


const Game = () => {
    const navigate = useNavigate();
    const [board, setBoard] = useState(Array(9).fill(null))
    const [xIsNext, setXIsNext] = useState(true)
    const [xcount, setXCount] = useState(0);
    const [count, setCount] = useState(0);
    const [draw, setDraw] = useState(draw)
    const [win, setWin] = useState(false)
    const winner = winCalc(board)
    const clickHandler = (index) => {
        const copyBoard = [...board]
        // Already clicked or not
        if (winner || copyBoard[index]) return
        // if (winner || copyBoard !=null) return
        copyBoard[index] = xIsNext ? 'X' : '0'
        // Who is next 'x' or '0'?
        // Refresh state
        setBoard(copyBoard)
        setXIsNext(!xIsNext)
        // setCount(count+1)
        if(xIsNext){
          setXCount(xcount+50)
        }
        if(!xIsNext){
          setCount(count+50)
        }
        if(!winner || copyBoard[index] && !winner && copyBoard[index] == xIsNext){
          return setDraw(true)
        }
    }
    function previous() {
      navigate('/home')
    }
    function game(){
      navigate('/result')
    }
    function next() {
      navigate('/data')
    }
    const card = {
      height: '85vh',
      marginBottom: '2px'
    }
    const startNewGame = ()=>{
        return(
            <button className='startBtn' onClick={()=> setBoard(Array(9).fill(null))}><p onClick={()=> setXCount(0)}><p onClick={()=> setCount(0)}>Clear field</p></p></button>
        )
    }
    return (
      <div className="">
      <div className="card w-96 text-primary-content">
        <div className='plannerstyle' style={{ backgroundImage: `url(${background})` }}>
        <div className="card-body" style={card}>
          <h2 className="card-title"></h2>
          <div className='wrapper'>
            {startNewGame()}
          <Board squares={board} click={clickHandler}/> 
          {/* <p className='gameInfo'>{winner ? 'Winner: '+winner : 'Next step: ' + (xIsNext ? 'X '+'Mary' : '0 '+'Paul')}</p>  */}
          <p className='gameInfo'>{winner ? 'Winner: '+winner : 'Next step: ' + (xIsNext ? 'X '+'Mary' : '0 '+'Paul')+draw}</p> 
        </div> 
          <div className="card-actions justify-end"></div>
          <div className="result1">
          {/* <p className='gameInfo'>{xIsNext ? 'Mary '+xcount : 'Mary '+xcount}</p>  */}
          <p className='gameInfo'>{'Mary: '+xcount+ " diamond's"}</p> 
          </div>
          <div className="result2">
          <p className='gameInfo'>{'Paul: '+count+" diamond's"}</p>
          </div>
        </div>
      </div>
      </div>
      </div>
    );
}

export default Game;
