import React from "react";
import { useNavigate } from "react-router-dom";
import background from '../images/game.png'
import Board from './Board';
import { useState } from 'react';
import { winCalc } from '../winCalc';


const Game = () => {
    const navigate = useNavigate();
    const [board, setBoard] = useState(Array(9).fill(null))
    const [xIsNext, setXIsNext] = useState(true)
    const [count, setCount] = useState(0);
    const [resX, setResX] = useState(count)
    const [res0, setRes0] = useState(count)
    const winner = winCalc(board)
    const clickHandler = (index) => {
        const copyBoard = [...board]
        // Already clicked or not
        if (winner || copyBoard[index]) return
        // if (winner || copyBoard !=null) return
        copyBoard[index] = xIsNext ? 'X' : '0'
        // Who is next 'x' or '0'?
        // Refresh state
        setBoard(copyBoard)
        setXIsNext(!xIsNext)
        // let resX = count;
        // let res0 = count;
        if (xIsNext){
          setResX(count + 1);
        }
        else{
          setRes0(count + 1);
        }

    }
    function previous() {
      navigate('/home')
    }
    function game(){
      navigate('/result')
    }
    function next() {
      navigate('/data')
    }
    const card = {
      height: '85vh',
      marginBottom: '2px'
    }
    const startNewGame = ()=>{
        return(
            // <button className='startBtn' onClick={()=> setBoard(Array(9).fill(null))}>Clear field</button>
            <button className='startBtn' onClick={()=> setBoard(0)}>Clear field</button>
        )
    }
    // count = xIsNext
    // const score = () => {
    //   setCount(count + 50);
    // };
    return (
      <div className="">
      <div className="card w-96 text-primary-content">
        <div className='plannerstyle' style={{ backgroundImage: `url(${background})` }}>
        <div className="card-body" style={card}>
          <h2 className="card-title"></h2>
          <div className='wrapper'>
            {startNewGame()}
          <Board squares={board} click={clickHandler}/> 
          <p className='gameInfo'>{winner ? 'Winner: '+winner : 'Next step: ' + (xIsNext ? 'X' : '0')}</p> 
        </div> 
          <div className="card-actions justify-end"></div>
          <div className="result1">
          <p className='gameInfo'>{winner ? 'Score: '+resX : 'Name: ' + (xIsNext ? 'X' : '0')}</p> 
          </div>
          <div className="result2">
          <p className='gameInfo'>{winner ? 'Score: '+res0 : 'Name: ' + (xIsNext ? 'X' : '0')}</p> 
          </div>
        </div>
      </div>
      </div>
      </div>
    );
}

export default Game;
