import React from 'react'
import Select from 'react-select'
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

// import React, { useContext} from 'react'
// import { AppContext } from '../App'

function Babyplanner() {
// const {data, setData} = useContext(AppContext)
  const navigate = useNavigate();
  
  // create use state for each select value
  const [data, setData] = useState({data})
  function clickHandler (e,a) {
    if(e.target.value == 'Girl' && a.target.value == 18){
      console.log(Sdata.baby.filter(i => i.id === '25'));
      
    }
    else{
      console.log("Something");
    }
    navigate('/dashboard')
  }
  function selectGenderHandler (e) {
    console.log(e.target.value);
  }
  function selectAgeHandler (a) {
    console.log(a.target.value);
  }
  
  return (
    <div className="card w-96 bg-primary text-primary-content">
  <div className="card-body">
    <h2 className="card-title">Babyplanner</h2>
    <p>Note: Your age should be in range 18-40 years</p>
    
        
<div className='gender-choice'>
        <select onChange={selectGenderHandler} className="select select-info w-full max-w-xs">
  <option disabled selected>Select baby's gender you wish</option>
  <option>Girl</option>
  <option>Boy</option>
</select>
</div>
<div className='age-choice'>
<select onChange={selectAgeHandler} className="select select-info w-full max-w-xs">
  <option disabled selected>Select your age</option>
  <option>18</option>
  <option>19</option>
  <option>20</option>
  <option>21</option>
  <option>22</option>
  <option>23</option>
  <option>24</option>
  <option>25</option>
  <option>26</option>
  <option>27</option>
  <option>28</option>
  <option>29</option>
  <option>30</option>
  <option>31</option>
  <option>32</option>
  <option>33</option>
  <option>34</option>
  <option>35</option>
  <option>36</option>
  <option>37</option>
  <option>38</option>
  <option>39</option>
  <option>40</option>
</select>
</div>
          {/* <div>Input gender, {data} 
    <input type={data} onChange={e=> setData(e.target.value)}/>
    </div>  */}
      <button onClick={clickHandler} className="btn">Enter</button> 
    <div className="card-actions justify-end">
    </div>
  </div>
  
</div>
  )
}

export default Babyplanner

