import React from 'react'
import { AppContext } from '../App';
import { useContext } from 'react';
import { useNavigate } from "react-router-dom";
import Graphics from './Graphics';
import pdfPic from '../pdf.png'
import housePic from '../house.png'
import babylightPic from '../babylight.png'


function Data() {
  const dataList = []
  // function printing (){
  //   // window.print()
  // }
  const printing = () => {
    result && result.months.map(i =>{
      dataList.push(i)
      console.log(dataList);
      dataList.map(i=>{
        console.log(i);
      })
      fetch(i).then(response => {
        response.blob(i).then(blob => {
            // Creating new object of PDF file
            // const fileURL = window.URL.createObjectURL(blob);
            const fileURL = window.URL.createObjectURL(blob);
            // Setting various property values
            let alink = document.createElement('a');
            alink.href = fileURL;
            alink.download = i;
            alink.click();
        })
    })
      // return i
    })
   
    // using Java Script method to get PDF file

    // fetch('SamplePDF.pdf').then(response => {
    //     response.blob(i).then(blob => {
    //         // Creating new object of PDF file
    //         // const fileURL = window.URL.createObjectURL(blob);
    //         const fileURL = window.URL.createObjectURL(blob);
    //         // Setting various property values
    //         let alink = document.createElement('a');
    //         alink.href = fileURL;
    //         alink.download = 'SamplePDF.pdf';
    //         alink.click();
    //     })
    // })
}





  const {result, setPagination} = useContext(AppContext)
  const navigate = useNavigate();
  function previous() {
    setPagination((i) => (i -= 1));
    navigate('/babyplanner')
  }
  function next() {
    setPagination((i) => (i += 1));
    navigate('/home')
  }
  
  return (
    <div>
       {<Graphics />}
      <div className='data'>
      {result && result.months.map(i => <h1>{i}</h1>)}
    </div>
      <div className="buttons">  
          <button onClick={previous} className="btn1">
          <img src={babylightPic} alt="" /> 
          </button>
          <button type="submit" className="btn2">
          <img src={pdfPic} alt="" />
          </button>
          <button onClick={next} className="btn3">
          <img src={housePic} alt="" />
          </button>
          </div>
          <button onClick={printing}>print result</button>
        </div>
  )
}

export default Data
