import React from 'react'
import '../App.css';
import { useDrag } from 'react-dnd';

function Pictures({id, url}) {
    const [{isDragging}, drag] = useDrag(() => ({
        type: 'image',
        item: {id: id},
        collect: (monitor)=>({
            isDragging: !!monitor.isDragging(),
        }),
    }));
  return (
    <img 
    ref={drag} 
    src={url} 
    width="150px" 
    height="150px" 
    style={{border: isDragging ? "5px solid pink" : "0px"}}/>


  );
  
}

export default Pictures
