import React from "react";
import { useNavigate } from "react-router-dom";
import background from '../images/treasuremap.png'
import Board from './Board';
import { useState } from 'react';
import { winCalc } from '../winCalc';
import shipPic from '../images/ship.png'
import pirategirlPic from '../images/pirategirl.png'


const Data = () => {
    const navigate = useNavigate();
    const [board, setBoard] = useState(Array(9).fill(null))
    const [xIsNext, setXIsNext] = useState(true)
    const [xcount, setXCount] = useState(0);
    const [count, setCount] = useState(0);
    const [draw, setDraw] = useState('Draw')
    const [win, setWin] = useState(false)
    const winner = winCalc(board)
    const clickHandler = (index) => {
        const copyBoard = [...board]
        // Already clicked or not
        if (winner || copyBoard[index]) return
        // if (winner || copyBoard !=null) return
        copyBoard[index] = xIsNext ? 'X' : '0'
        // Who is next 'x' or '0'?
        // Refresh state
        setBoard(copyBoard)
        setXIsNext(!xIsNext)
        // setCount(count+1)
        // if(xIsNext){
        //   setXCount(xcount+50)
        // }
        // if(!xIsNext){
        //   setCount(count+50)
        // }
        // if(!winner || copyBoard[index] && !winner && copyBoard[index] == xIsNext){
        //   return setDraw('Draw')
        // }
        if(xcount === 5 || count === 5){
          return setDraw(draw)
        }
    }
    function previous() {
      navigate('/home')
    }
    function game(){
      navigate('/result')
    }
    function next() {
      navigate('/data')
    }
    const card = {
      height: '85vh',
      marginBottom: '2px'
    }
    return (
      <div className="">
      <div className="card w-96 text-primary-content">
        <div className='plannerstyle' style={{ backgroundImage: `url(${background})` }}>
        <div className="card-body" style={card}>
          <h2 className="card-title"></h2>
          <div className='showResult'>
          <p className='gameResult'>{'Winner: ' + localStorage.getItem('Winner')}</p> 
        </div> 
          <div className="buttons">  
          <button onClick={previous} className="btn2">
          <img src={shipPic} alt="" />
          </button>
          <div className="res">
          {/* <p className='showResult'>{'Winner: ' + localStorage.getItem('Winner')}</p>  */}
          <p className='gameSteps'>Highscore:</p> 
          <div className="res1">
          {/* <p className='gameInfo'>{'Mary: '+xcount+ " diamond's"}</p>  */}
          <p className='gameInfo'>{'Mary '+localStorage.getItem('Mary')+ " diamond's"}</p> 
          </div>
          <div className="res2">
          {/* <p className='gameInfo'>{'Paul: '+count+" diamond's"}</p> */}
          <p className='gameInfo'>{'Paul '+localStorage.getItem('Paul')+ " diamond's"}</p> 
          </div>
          </div>
          <button onClick={next} className="btn2">
          <img src={pirategirlPic} alt="" /> 
          </button>
          </div>
        </div>
      </div>
      </div>
      </div>
    );
}

export default Data;
