import React from 'react'
import { AppContext } from '../App';
import { useContext } from 'react';
import { useNavigate } from "react-router-dom";

function Login() {
    const {login, user, users, setUser, setUsers, notify} = useContext(AppContext)
  const navigate = useNavigate();
  function loginSubmit(e){
    e.preventDefault()
    console.log(e.target.username.value)
    let u = e.target.username.value
    let p = e.target.password.value
  let foundUser = users.find(i=> i.name =u)
  console.log(foundUser)
  if(!foundUser){
    return
  }
  if(foundUser.password==p){
    setUser(foundUser)
    notify(foundUser.name)
  }
   }
   useEffect(()=>{
    if(user){
      navigate('/personal')
    }
   }, [user])
  return (
    <div className="card w-96 bg-primary text-primary-content">
  <div className="card-body">
    <h2 className="card-title">Login</h2>
    <p>Enter your login and password to continue</p>
    <form onSubmit={loginSubmit}>
        <input type='text' name='username'
        placeholder='type here'
        className='input input-bordered w-full max-w-xs'/>
        <input name="password" 
        type='password'
        placeholder='Type here'
        className='input input-bordered w-full max-w-xs'/>
      <button className="btn">Login</button> 
      </form>
    <div className="card-actions justify-end">
    </div>
  </div>
</div>
  )
}

export default Login
