import "./App.css";
import Names from "./components/Names";
import json from "./data.json";
import Login from "./components/Login";
import Home from "./components/Home";
import Groups from "./components/Groups";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Absence from "./components/Absence";
import InputForm from "./components/InputForm";
import { Routes, Route, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { createContext } from "react";
export const AppContext = createContext();

function App() {
  const [date, setDate] = useState(json);
  const [selectedGroup, setSelectedGroup] = useState();

  const [user, setUser] = useState();
  const [users, setUsers] = useState(json);
  const navigate = useNavigate();

  function login(e) {
    e.preventDefault();
    let u = e.target.username.value;
    let p = e.target.password.value;

    let foundUser = users.find((i) => i.parentName == u);
    if (!foundUser) {
      return;
    }

    if (foundUser.password == p) {
      setUser(foundUser);
      // notify(foundUser.name);
    }
  }

  useEffect(() => {
    if (user) {
      navigate("/groups");
    }
  }, [user]);

  return (
    <div
      className="h-screen flex flex-col  justify-between items-center"
      data-theme="cupcake"
    >
      {<Navbar />}

      <AppContext.Provider
        value={{ date, setDate, selectedGroup, setSelectedGroup }}
      >
        <Routes>
          <Route path="/" element={<Login users={users} login={login} />} />
          <Route path="/groups" element={<Groups />} />
          <Route path="/names" element={<Names />} />
          <Route path="/absence" element={<Absence />} />
          <Route path="/inputform" element={<InputForm />} />



        </Routes>
      </AppContext.Provider>

      {<Footer />}
    </div>
  );
}

export default App;
