import React from 'react'
import Babyplanner from './Babyplanner'
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { createContext } from 'react';
import { AppContext } from '../App';
import { useContext } from 'react';
import Dashboard from './Dashboard';
import DataChart from './DataChart';


function Data() {
    const [showText, setShowText] = useState(false)
    const {data, setData} = useContext(AppContext)
  const femaleFilter = () =>{
    let babyGender = data.baby.filter(i => i.gender === 'Female')
    // let babyGender = json.baby.filter(i => i.id === {option})
    // let babyGender = json.baby.filter(i => i.id === '25')
    setData(babyGender)
  }
  const maleFilter = () =>{
    let babyGender = data.baby.filter(i => i.gender === 'Male')
    setData(babyGender)
  }
  return (
    <div>Data: 
        <AppContext.Provider value={{
      data,
      setData
     }}>
      </AppContext.Provider>
         <p onClick={() => {setShowText(!showText)}}><p onClick={femaleFilter}>Female</p></p>
      <p onClick={() => {setShowText(!showText)}}><p onClick={maleFilter}>Male</p></p>
      {showText === true && <Dashboard data ={data} />} 
      {showText === true &&  <DataChart />}
        
        </div>
  )
}

export default Data
