  // if(localStorage.getItem('Winners') || localStorage.getItem('Winners list')){
  //   // winners.push(localStorage.getItem('Winners'))
  //   const copyWin = [...winners]
  //   copyWin.push(localStorage.getItem('Winners'))
  //   localStorage.setItem('Winners list', copyWin)
  // }
  // if(localStorage.getItem('Winners list')){
  //   // localStorage.removeItem('Winners');
  // }


  // History
  // function winHandler(e){
  //   e.preventDefault();
  //   if (
  //     e.target.localStorage.getItem('Winners').value === user1 || e.target.localStorage.getItem('Winners').value === user2
  //   ) 
  //   {
  //     return;
  //   }
  //   const found = e.target.localStorage.getItem('Winners').value
    
  //   {
  //     if (found){
  //       const copyWin = [...win]
  //       copyWin.push(localStorage.getItem('Winners'))
  //       setWin(copyWin)
  //       localStorage.setItem('Winners List', copyWin)
  //     return found
  //   }
  //   }
  // }
  
  // Draw checker
  if (!winner && !board.includes(null)) {
    setDraw(true)
    localStorage.removeItem('Winners');
    localStorage.setItem(user1, 0)
    localStorage.setItem(user2, 0)
    setDrawScore(Number(drawScore))
    localStorage.setItem('DrawScore', drawScore+1)
    setBoard(board)
  }

  // // User VS Computer
  // if(user1 === 'Computer' || user2 === 'Computer'){

  // }
  const checkOpenCells = (board) =>{
    let copy = [...board]
    return copy.reduce((open, tile, index) =>{
        if(tile === -9){
            open.push(index);
        }
        return open;
    },[])
}
const playAI = (current_board) =>{
    let boardCopy = [...current_board];
    const open_tiles = checkOpenCells(boardCopy);
    // This is avaliable space
    if(open_tiles.length){
const rand = Math.random() * open_tiles.length;
return open_tiles[Math.floor(rand)];
    }
    return null;
}

// History
const history = []
history.push(localStorage.getItem('Winners'))
const copyHistory = [...wincount]
copyHistory.push(localStorage.getItem('Winners'))

  // WinChecker
  if(winner === 'X'){
    setXCount(xcount + 4.5)
    localStorage.setItem(user1, Math.round(xcount + 4.5)); 
    localStorage.setItem('Winners', user1)
    setXScore(Number(xscore))
    localStorage.setItem('XScore', Number(xscore+1))
    setWincount(wincount)
    localStorage.setItem('Winlist', (copyHistory.push(localStorage.getItem('Winners'))))
    localStorage.removeItem('Draw'); 
  }
 
  if(winner === '0'){
      setCount(count + 4.5)
      localStorage.setItem(user2, Math.round(count + 4.5));
      localStorage.setItem('Winners', user2)
      setScore(Number(score))
      localStorage.setItem('Score', Number(score+1))
      setWincount(copyHistory)
      localStorage.setItem('Winlist', (copyHistory.push(localStorage.getItem('Winners'))))
      localStorage.removeItem('Draw');

      // localStorage.setItem('Winners List', win)

    }
     
  const clickHandler = (index) => {
    const copyBoard = [...board]
    // Already clicked or not
    if (copyBoard[index]) {
      return
    }
    copyBoard[index] = xIsNext ? 'X' : '0'
      // User VS Computer
  if(user1 === 'Computer' || user2 === 'Computer'){
  playAI()
  }
