import React from 'react'
import { AppContext } from '../App';
import { useContext } from 'react';
import { useNavigate } from "react-router-dom";
import katzePic from '../katze.png'
import babylightPic from '../babylight.png'
import settingsPic from '../settings.png'

function Home() {
  const {setPagination, setCity} = useContext(AppContext)
  const { kdata, setResKdata, data, setResult} = useContext(AppContext);
  const navigate = useNavigate();
  function home() {
    navigate('/home')
  }
  function babyplanner() {
    navigate('/babyplanner')
  }
  // function data() {
  //   navigate('/data')
  // }
  function profile() {
    navigate('/personal')
  }
  function previous() {
    setPagination((i) => (i -= 1));
    navigate('/settings')
    // acsess to settings
  }
  function next() {
    setPagination((i) => (i += 1));
    navigate('/babyplanner')
  }
  function submitLocationHandler(e){
    e.preventDefault();
    if (
      e.target.citySelect.value == "Select your location" 
    ) 
    {
      alert("Please select location");
      return;
    }
    const foundCity = e.target.citySelect.value
    
   
    {
      if (foundCity){
      setCity(foundCity)
      navigate('/babysitters')
      return foundCity
    }
      
    }
  }

  function kindergardenLocationHandler(e){
    e.preventDefault();
    if (
      e.target.citySelect.value == "Select your location" 
    ) 
    {
      alert("Please select location");
      return;
    }
    const foundCity = e.target.citySelect.value
   
    {
      if (foundCity){
      setCity(foundCity)
      navigate('/kindergardens')
      return foundCity
    }
  }
}

// function kindergardenLocationHandler(e) {
//   e.preventDefault();
//   if (
//     e.target.citySelect.value == "Select your location" 
//   ) {
//     alert("Please select location");
//     return;
//   }
//   const id = e.target.citySelect.value
//   // navigate('/kindergardens')
//   console.log(id);
//   const foundId = kdata.kindergarden.find(i => { 
//     console.log(i.id);
//     console.log(i.list[0]);
//     return i.id == id 
//   })
  
//   if (foundId){
//     setResKdata(foundId)
//     navigate('/kindergardens')
//   }
 
// }
  
  
  return (
    <div>
       <div>
        <img src={katzePic} alt="" /> 
    </div>
      <div className="buttons">  
          <button onClick={previous} className="btn2">
          <img src={settingsPic} alt="" /> 
          </button>
          <form onSubmit={submitLocationHandler}>
          <div className="choice-form">
          <div className="city-choice">
            <select
              name="citySelect"
              className="select select-info w-full max-w-xs"
            >
              <option disabled selected>
                Select your location
              </option>
              <option>Munchen</option>
              <option>Nurnberg</option>
              <option>Berlin</option>
              <option>Hamburg</option>
              <option>Frankfurt</option>
            </select>
          </div>
          </div>
          <button type="submit" className="btn">
            Available babysitters
          </button>
        </form>

        <form onSubmit={kindergardenLocationHandler}>
          <div className="choice-form">
          <div className="city-choice">
            <select
              name="citySelect"
              className="select select-info w-full max-w-xs"
            >
              <option disabled selected>
                Select your location
              </option>
              <option>Munchen</option>
              <option>Nurnberg</option>
              <option>Berlin</option>
              <option>Hamburg</option>
              <option>Frankfurt</option>
            </select>
          </div>
          </div>
          <button type="submit" className="btn">
            Available kindergardens
          </button>
        </form>
         
          <button onClick={next} className="btn2">
          <img src={babylightPic} alt="" /> 
          </button>
          </div>
    </div>
  )
}

export default Home
