import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";

function Groups({ user }) {

  const { date, setDate, setSelectedGroup, selectedGroup } =
    useContext(AppContext);
  console.log(date);

  const navigate = useNavigate();
//   useEffect(() => {
//     // if (!user) {
//     //     navigate ("/login");
//     // }
//   }, []);

  useEffect(() => {
    if (selectedGroup) {
      navigate("/names");
    }
  }, [selectedGroup]);


  function selectingGroup(e) {
    const group = date.find(i => i.id == e.target.value)

    setSelectedGroup(group);
    
  }

  return (
    <div className="btn-group btn-group-vertical">
    
      {date.map((i) => (
        <button className="btn btn-wide" onClick={selectingGroup} value={i.id}>
          {i.groupName}
        </button>
      ))}
      
    </div>
  );
}

export default Groups;
