import React from 'react'
import Babyplanner from './Babyplanner'
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { createContext } from 'react';
import { AppContext } from '../App';
import { useContext } from 'react';

function Data() {
    const [showText, setShowText] = useState(false)
  const [data, setData] = useState(json)
  const femaleFilter = () =>{
    let babyGender = json.baby.filter(i => i.gender === 'Female')
    // let babyGender = json.baby.filter(i => i.id === {option})
    // let babyGender = json.baby.filter(i => i.id === '25')
    setData(babyGender)
  }
  const maleFilter = () =>{
    let babyGender = json.baby.filter(i => i.gender === 'Male')
    setData(babyGender)
  }
  return (
    <div>Data: 
        <AppContext.Provider value={{
      data,
      setData
     }}>
<Routes>
  <Route path='/home' element={<Home />}/>
  <Route path='/babyplanner' element={<Babyplanner />}/>
  <Route path='/dashboard' element={<Dashboard />}/>
  <Route path='/data' element={<Data />}/>
 </Routes>
      </AppContext.Provider>
         <p onClick={() => {setShowText(!showText)}}><p onClick={femaleFilter}>Female</p></p>
      <p onClick={() => {setShowText(!showText)}}><p onClick={maleFilter}>Male</p></p>
      {showText === true && <Dashboard data ={data} />} 
      {showText === true &&  <DataChart />}
        
        </div>
  )
}

export default Data
