import React from 'react'
import { useState } from 'react'
import Pictures from './Pictures'
import { useDrop } from 'react-dnd'
import { AppContext } from '../App';

const PictureList =[
    {
        id: 1,
        url: 'https://images.unsplash.com/photo-1674591409662-55f4f2c55fff?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1374&q=80'
    },
    {
        id: 2,
        url: 'https://plus.unsplash.com/premium_photo-1667682998369-1d1ea5b253c2?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1469&q=80'
    },
    {
        id: 3,
        url: 'https://images.unsplash.com/photo-1672700003367-90812f0379d0?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=809&q=80'
    },
    {
        id: 4,
        url: 'https://images.unsplash.com/photo-1674530493752-719b5514a7f2?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=929&q=80'
    }
]
function DragDrop() {
    const {kdata} = useContext(AppContext)
    const [board, setBoard] = useState([])
    const [{isOver}, drop] = useDrop(() => ({
        accept: 'image',
        drop: (item) => addImageToBoard(item.id),
        collect: (monitor)=>({
            isOver: !!monitor.isOver(),
        }),
    }))
    const addImageToBoard = (id)=>{
        const PictureList = PictureList.filter((picture) => id === picture.id);
        // setBoard((board) =>[...board, PictureList[0]])
        setBoard([PictureList[0]])
console.log(id);
    }
  return (
    <>
    <div className='Pictures'> {PictureList.map((picture) => {
        return <Pictures url={picture.url} id={picture.id}/>
    })}
    </div>
    <div className='Board' ref={drop}>
        {board.map((picture) =>{
          return <Pictures url={picture.url} id={picture.id}/>  
        })}
    </div>
    </>
  )
}

export default DragDrop
