import React from 'react'
import { AppContext } from '../App';
import { useContext } from 'react';
import { useNavigate } from "react-router-dom";
import Graphics from './Graphics';


function Data() {
  const dataList = []
  // function printing (){
  //   // window.print()
  // }
  const printing = () => {
    result && result.months.map(i =>{
