        response.blob(da).then(blob => {
            // Creating new object of PDF file
            // const fileURL = window.URL.createObjectURL(blob);
            const fileURL = window.URL.createObjectURL(blob);
            // Setting various property values
            let alink = document.createElement('a');
            alink.href = fileURL;
            alink.download = 'SamplePDF.pdf';
            alink.click();
        })
    })
}





  const {result, setPagination} = useContext(AppContext)
  const navigate = useNavigate();
  function previous() {
    setPagination((i) => (i -= 1));
    navigate('/babyplanner')
  }
  function next() {
    setPagination((i) => (i += 1));
    navigate('/home')
  }
  
  return (
    <div>
       {<Graphics />}
      <div className='data'>
      {result && result.months.map(i => <h1>{i}</h1>)}
    </div>
      <div className="buttons">  
          <button onClick={previous} className="btn1">
            Babyplanner
          </button>
          <button type="submit" className="btn">
            Save result
          </button>
          <button onClick={next} className="btn2">
            Home
          </button>
          </div>
          <button onClick={printing}>print result</button>
        </div>
  )
}

export default Data
