import React, { useEffect, useState } from "react";
import Absence from "./Absence";
import InputForm from "./InputForm";
import { AppContext } from "../App";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";



function Names() {

  const { data, selectedGroup, setSelectedGroup } = useContext(AppContext);

  const findId = data.find((i) => i.id == selectedGroup);
  const navigate = useNavigate();

  function handleClick() {
    navigate("/absence ");
  }
  const dataList = []
  function clickHandler () {
    
    // dataList.forEach(element => {
    //   if (element[0]==='Girl' && element[1]===18){
    //         console.log("hi");
    //       }
    // });
   
     console.log(dataList);
      // console.log(setData(data.baby.filter(i => i.id === '25'))); 
    
    // else{
    //   console.log("Something");
    // }
    navigate('/data')
  }
  function selectGenderHandler (e) {
    let result
    result = e.target.value
    dataList.push(result)
    return result
    // console.log(e.target.value);
  }
  function selectAgeHandler (a) {
    let result
    result = a.target.value
    dataList.push(result)
    return result
    // console.log(a.target.value);
  }



  return (
    <div>
 <div className="flex flex-col w-full border-opacity-50">
  <div className="grid h-20 card bg-base-300 rounded-box place-items-center">{selectedGroup.groupName}</div>
  

        {/* <h1>{selectedGroup.groupName}</h1> */}
        {/* <h2>The annoying child is</h2>   */}
        {selectedGroup.children.map(i => 
        
        <button type="submit"
        className="btn" onClick={handleClick} >{i.name}</button>)}


      
        </div>
        <div className="card w-96 bg-primary text-primary-content">
  <div className="card-body">
    <h2 className="card-title">Babyplanner</h2>
    <p>Note: Your age should be in range 18-40 years</p>
    
        
<div className='gender-choice'>
        <select onChange={selectGenderHandler} className="select select-info w-full max-w-xs">
  <option disabled selected>Select baby's gender you wish</option>
  <option>Girl</option>
  <option>Boy</option>
</select>
</div>
<div className='age-choice'>
<select onChange={selectAgeHandler} className="select select-info w-full max-w-xs">
  <option disabled selected>Select your age</option>
  <option>18</option>
  <option>19</option>
  <option>20</option>
  <option>21</option>
  <option>22</option>
  <option>23</option>
  <option>24</option>
  <option>25</option>
  <option>26</option>
  <option>27</option>
  <option>28</option>
  <option>29</option>
  <option>30</option>
  <option>31</option>
  <option>32</option>
  <option>33</option>
  <option>34</option>
  <option>35</option>
  <option>36</option>
  <option>37</option>
  <option>38</option>
  <option>39</option>
  <option>40</option>
</select>
</div>
          {/* <div>Input gender, {data} 
    <input type={data} onChange={e=> setData(e.target.value)}/>
    </div>  */}
      <button onClick={clickHandler} className="btn">Enter</button> 
    <div className="card-actions justify-end">
    </div>
  </div>
  
</div>
    </div>
  );
}

export default Names;
