import React from "react";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";
import { useContext } from "react";
import background from '../images/game.png'
import shipPic from '../images/ship.png'
import pirategirlPic from '../images/pirategirl.png'
import Square from './Square';

function Game() {
  const { data, setResult, setPagination } = useContext(AppContext);
  const navigate = useNavigate();

  function previous() {
    setPagination((i) => (i -= 1));
    navigate('/home')
  }
  function game(){
    navigate('/result')
  }
  function next() {
    setPagination((i) => (i += 1));
    navigate('/data')
  }
  const card = {
    height: '85vh',
    marginBottom: '2px'
  }
  const Board = ({squares, click}) => {
    return (
        <div className='board'>
          {
            squares.map((square, i) =>(
                <Square key={i} value={square} onClick={()=> click(i)}/>
            ))
          }
        </div>
    );
}

  return (
    <div className="">
    <div className="card w-96 text-primary-content">
      <div className='plannerstyle' style={{ backgroundImage: `url(${background})` }}>
      <div className="card-body" style={card}>
        <h2 className="card-title"></h2>
     
          {/* <div className="buttons">  
          <button onClick={previous} className="btn2">
          <img src={shipPic} alt="" />
          </button>
          <h1 onClick={game} type="submit" className="btn1">
          <h1>Start</h1>
          </h1>
          <button onClick={next} className="btn2">
          <img src={pirategirlPic} alt="" /> 
          </button>
          </div> */}
        <div className="card-actions justify-end"></div>
      </div>
    </div>
    </div>
    </div>
  );
}

export default Game;
