import React from "react";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";
import { useContext } from "react";
import background from '../images/main.png'
import shipPic from '../images/ship.png'
import pirategirlPic from '../images/pirategirl.png'
import { useState } from "react";

function Home() {
  const navigate = useNavigate();
  const users = [localStorage.getItem('New user')]
  console.log(users);
      const user = users.map((i) => (
            i.split(',')
          ))
  console.log(user);
  for (let i = 0; i<user.length; i++){
    console.log(i)
    return [i]
  }

  function game() {
    navigate('/game')
  }
  function next() {
    navigate('/data')
  }
  const card = {
    height: '85vh',
    marginBottom: '2px'
  }

  function submitHandler(e) {
    e.preventDefault();
    if (
      e.target.userSelect.value == "Select participant"
    ) {
      alert("Please select participant");
      return;
    }
    const foundUser = e.target.userSelect.value

    {
      if (foundUser) {
        game()
        return foundUser
      }

    }

  //   const userList = []
  //   userList.push(localStorage.getItem('New user'))
  //   console.log(userList)

  //   for (let i = 0; i<userList.length; i++){
  //     console.log(userList)
  //     console.log(i)
  //     console.log([i])
  //     console.log(userList[i])
  //        return userList[i]
  // }

  }

  return (
    <div className="">
      <div className="card w-96 text-primary-content">
        <div className='plannerstyle' style={{ backgroundImage: `url(${background})` }}>
          <div className="card-body" style={card}>
            <h2 className="card-title"></h2>
            <div className="home-buttons">
              <form className="form" onSubmit={submitHandler}>
                    <select name="userSelect" className="select w-24">
                      <option disabled selected>Select participant</option>       
      {user.map((i) => (
             <option>{i[0]}</option>
          ))}
      {user.map((i) => (
             <option>{i[1]}</option>
          ))}
      {user.map((i) => (
             <option>{i[2]}</option>
          ))}
      {user.map((i) => (
             <option>{i[3]}</option>
          ))}


                    </select>
                  <div className="user-choice">
                  <h1 onClick={game} type="submit" className="btn1">
                  <h1>Start</h1>
                </h1>
                  </div>
                <div className="choice-form">
                <button onClick={next} className="btn2">
                  <img src={pirategirlPic} alt="" />
                </button>
                </div>
              </form>
            </div>
            <div className="card-actions justify-end"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
